from collections import Counter

ENCRYPTED = ""
KEY_LENGTH = 6
ENCRYPTED_LENGTH = len(ENCRYPTED)
TESS26 = open("data/tess26.txt", "r").readline()

same_letter_segments = [ENCRYPTED[i::6] for i in range(0, 6)]

tess26Counter = Counter(TESS26)
segmentCounters = [Counter(segment) for segment in same_letter_segments]

print([ord(kvp[0]) for kvp in tess26Counter.most_common()])
print([ord(kvp[0]) for kvp in segmentCounters[0].most_common()])

